// LOAD PRODUCT DETAIL
// ========================================
function loadProductDetail() {
    const urlParams = new URLSearchParams(window.location.search);
    const cat = parseInt(urlParams.get('cat')) || 1;
    const id = parseInt(urlParams.get('id')) || 1;
    
    if (!categoriesData[cat] || !categoriesData[cat].products[id - 1]) {
        document.body.innerHTML = '<div class="container" style="padding: 100px 60px;"><h1>Продукт не найден</h1></div>';
        return;
    }
    
    const product = categoriesData[cat].products[id - 1];
    const category = categoriesData[cat];
    
    // Update title and breadcrumbs
    document.getElementById('productTitle').textContent = product.name;
    document.getElementById('breadcrumbCategory').textContent = category.title;
    document.getElementById('breadcrumbCategory').href = `product-category.html?cat=${cat}`;
    document.getElementById('breadcrumbProduct').textContent = product.name.substring(0, 50) + '...';
    document.title = product.name + ' - Pascal Medical';
    
    // Load single image (БЕЗ СЛАЙДШОУ)
    document.getElementById('productImage').src = `img/img_products_1/${product.image}`;
    document.getElementById('productImage').alt = product.name;

    
    // Load characteristics
    const charGrid = document.getElementById('characteristics');
    charGrid.innerHTML = '';
    Object.entries(product.characteristics).forEach(([label, value]) => {
        const item = document.createElement('div');
        item.className = 'characteristic-item';
        item.innerHTML = `
            <div class="characteristic-label">${label}</div>
            <div class="characteristic-value">${value}</div>
        `;
        charGrid.appendChild(item);
    });
    
    // Load description
    document.getElementById('descriptionText').textContent = product.description;
}

document.addEventListener('DOMContentLoaded', loadProductDetail);

// Phone copy
const phoneBtn = document.getElementById('phoneBtn');
const footerPhoneBtn = document.getElementById('footerPhoneBtn');
const toast = document.getElementById('toast');

function copyPhoneNumber(button) {
    const phoneNumber = button.getAttribute('data-phone');
    if (navigator.clipboard) {
        navigator.clipboard.writeText(phoneNumber).then(() => {
            toast.classList.add('show');
            setTimeout(() => toast.classList.remove('show'), 2800);
        });
    }
}

if (phoneBtn) phoneBtn.addEventListener('click', (e) => {
    e.preventDefault();
    copyPhoneNumber(phoneBtn);
});

if (footerPhoneBtn) footerPhoneBtn.addEventListener('click', (e) => {
    e.preventDefault();
    copyPhoneNumber(footerPhoneBtn);
});

// Search
const searchBtn = document.getElementById('searchBtn');
const searchOverlay = document.getElementById('searchOverlay');
const searchClose = document.getElementById('searchClose');

if (searchBtn) {
    searchBtn.addEventListener('click', (e) => {
        e.stopPropagation();
        searchOverlay.classList.add('active');
    });
}

if (searchClose) {
    searchClose.addEventListener('click', () => {
        searchOverlay.classList.remove('active');
    });
}
